html{font-family:sans-serif;-ms-text-size-adjust:100%;-webkit-text-size-adjust:100%}body{margin:0}header,nav{display:block}a{background-color:transparent}img{border:0}html{font-size:100%;line-height:1.5em}html,body{height:100%}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}body{background:#fff;color:#333;font-family:"Open Sans",sans-serif;margin:0;padding:0;font-size:1em;line-height:1.5em}@media all and (max-width: 640px){body{font-size:80%}}img{border:0;vertical-align:bottom;max-width:100%}img.full{width:100%}header{width:100%;color:#fff;padding:0 10px;height:120px}.subheader{margin:0 auto;max-width:1200px;text-transform:uppercase;font-weight:200;text-align:center;height:100%}.subheader:after{content:"";display:table;clear:both}.subheader-left{float:left;background:url("../images/dist/logo-white.png") 20px center no-repeat #ed1c24;background-size:auto 80%;height:100%;width:145px}@media all and (max-width: 360px){.subheader-left{float:none}}.subheader-right{float:right;position:relative;top:50%;-webkit-transform:translateY(-50%);-ms-transform:translateY(-50%);transform:translateY(-50%)}@media all and (max-width: 360px){.subheader-right{float:none;padding:5px 0 0 0}}.subheader-right a{color:inherit;text-decoration:inherit;cursor:inherit}.subheader-right>div{display:inline}.subheader-right span{font-weight:normal}.subheader-right span.active{font-weight:bold}.subheader-right i{padding:0 10px}.subheader-right i:first-child{padding:0 10px 0 20px}.subheader-right i:last-child{padding:0 0 0 10px}nav{width:100%;background:#333;color:#fff;position:relative;padding:10px 0}.subnav{margin:0 auto;max-width:1200px;text-transform:uppercase}.subnav:after{content:"";display:table;clear:both}.subnav i{display:none}@media all and (max-width: 900px){.subnav i{display:block;padding:10px;font-size:1.25em;line-height:1.2em}}.subnav ul,.subnav li{margin:0;padding:0}.subnav ul:after{content:"";display:table;clear:both}@media all and (max-width: 900px){.subnav ul{display:none}}.subnav li{float:left;list-style:none;width:16.66666666666667%;text-align:center}@media all and (max-width: 900px){.subnav li{width:100%}.subnav li:last-child{padding:0 0 20px}}.subnav li a{color:inherit;text-decoration:inherit;cursor:inherit;font-size:1.5em;line-height:2em;font-weight:200;display:block}.bx-wrapper{position:relative;margin:0 auto 30px;padding:0;*zoom:1}.bx-wrapper img{max-width:100%;display:block}.bx-wrapper .bx-viewport{box-shadow:none;border:0 solid transparent;left:0;background:#fff;-webkit-transform:translatez(0);-ms-transform:translatez(0);transform:translatez(0)}.topslider{margin:0;padding:0}.topslider li{margin:0;padding:0}.topslider .overlay-text{position:absolute;top:0;left:0;width:100%;height:100%;z-index:2;background-position:center center;background-repeat:no-repeat;background-color:transparent;background-size:cover}.topslider .overlay-text.first{background-image:url("../images/dist/slider-top/text-1.png")}.topslider .overlay-text.second{background-image:url("../images/dist/slider-top/text-2.png")}.topslider .overlay-text.third{background-image:url("../images/dist/slider-top/text-3.png")}.topslider .overlay-text.fifth{background-image:url("../images/dist/slider-top/text-5.png")}